import React, { useState } from 'react'
import auth0 from '../lib/auth0'

const CreateStatus = () => {
  const getMyLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(position => {
        console.log(position.coords)
      })
    }
  }
  return (
    <div>
      <h1>Criar status</h1>
      <label className='block'>
        <input type='radio' name='status' value='bem' /> Estou bem e sem
        sintomas.
      </label>
      <label className='block'>
        <input type='radio' name='status' value='gripe' /> Estou com sintomas de
        gripe / refriado.
      </label>
      <label className='block'>
        <input type='radio' name='status' value='covid' /> Estou com sintomas da
        COVID.
      </label>
      Sua posição atual: {lat}x{long}
      <button onClick={getMyLocation}>Minha localição</button>
    </div>
  )
}

export default CreateStatus

export async function getServerSideProps ({ req, res }) {
  const session = await auth0.getSession(req)
  if (session) {
    return {
      props: {
        isAuth: true,
        user: session.user
      }
    }
  }
  return {
    props: {
      isAuth: false,
      user: {}
    }
  }
}
